<!--compressed bart lmao--><!DOCTYPE html><html><head><title>bart</title><style>body{background: url('bg.webp') no-repeat fixed;background-size: cover;font-family: Arial, sans-serif;margin: 0;display: flex;align-items: center;justify-content: center;height: 100vh;} .container{background-color: rgba(255, 255, 255, 0.8);border-radius: 15px;height: 80%;width: 75%;padding: 20px;box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);display: flex;flex-direction: column;align-items: center;} .topbar{background-color: #ffbf00;color: white;padding: 10px;width: 100%;border-top-left-radius: 15px;border-top-right-radius: 15px;margin-bottom: 20px;display: flex;align-items: center;justify-content: center;}input, textarea{background-color: #ffbf00;} .content{display: flex;width: 100%;} .recent-barts, .create-form{flex: 1;text-align: center;margin-right: 20px;}table{width: 100%;border-collapse: collapse;} th, td{padding: 15px;text-align: left;border-bottom: 1px solid #ddd;} th{background-color: #ffbf00;color: white;} a{color: black;text-decoration: none;} a:hover{text-decoration: underline;} .create-form form{display: flex;flex-direction: column;align-items: center;} .create-form input, .create-form textarea{margin-bottom: 10px;padding: 8px;width: 100%;} .create-form button{padding: 10px;background-color: #ffbf00;color: white;border: none;cursor: pointer;width: 100%;}</style></head><body><div class="container"><div class="topbar"><img width="100px" src="bart.webp"><h1>bart</h1></div><div class="content"><div class="recent-barts"><h2>view barts</h2><table><tbody><?php $bartFolder = 'check'; $barts = scandir($bartFolder); $currentPage = $_GET['page'] ?? 1; $bartsPerPage = 5; $start = ($currentPage - 1) * $bartsPerPage; $barts = array_slice($barts, $start, $bartsPerPage); foreach ($barts as $bart) { if ($bart != '.' && $bart != '..') { echo "<tr><td><a href='$bartFolder/$bart'>$bart</a></td></tr>"; } } ?> </tbody></table><div><br><br><br><br><?php $totalPages = ceil(count($barts) / $bartsPerPage); for ($i = 1; $i <= $totalPages; $i++) { echo "<a href='?page=$i'>$i</a> "; } ?> </div></div><div class="create-form"><h2>create bart</h2><form action="" method="post"><label for="bartName">Bart Name:</label><input type="text" id="bartName" name="bartName" required><label for="htmlCode">HTML Code:</label><textarea id="htmlCode" name="htmlCode" rows="4" required></textarea><button type="submit" name="createBart">Create Bart</button></form></div></div></div><?php if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['createBart'])) { $bartName = strtolower(preg_replace('/[^a-z0-9]+/', '-', $_POST['bartName'])); $htmlCode = $_POST['htmlCode']; $newbartFolder = $bartFolder . '/' . $bartName; if (!file_exists($newbartFolder)) { mkdir($newbartFolder); file_put_contents($newbartFolder . '/index.html', $htmlCode); echo "<script>alert('new bart created, please wait for approval');</script>"; } else { echo "<script>alert('hey stupid! that bart already exists!');</script>"; } } ?></body></html>